    function _update(address from, address to, uint256 value) internal {
        if (from == address(0)) {
            balances[to] += value;
            totalSupply += value;
            emit Transfer(address(0), to, value);
            return;
        }

        if (to == address(0)) {
            balances[from] -= value;
            totalSupply -= value;
            emit Transfer(from, address(0), value);
            return;
        }

        revert("Transfers between users not supported");
    }

    function _updateTeamInvestmentValues(
        address user,
        uint256 amount,
        bool isIncrease
    ) internal {
        address[] memory referralChain = getReferrals(user, maxD);
        for (uint8 i = 0; i < referralChain.length; ) {
            unchecked {
                if (isIncrease) {
                    teamTotalInvestValue[referralChain[i]] += amount;
                } else {
                    teamTotalInvestValue[referralChain[i]] -= amount;
                }
                ++i;
            }
        }
    }

    function _syncExistingInvestmentToReferralChain(
        address user,
        uint256 existingInvestment
    ) internal {
        address[] memory referralChain = getReferrals(user, maxD);
        for (uint8 i = 0; i < referralChain.length; ) {
            unchecked {
                teamTotalInvestValue[referralChain[i]] += existingInvestment;
                ++i;
            }
        }
    }

    function _updateRatesForMode() internal {
        rates[0] = getAPYRate1D();
        rates[1] = getAPYRate30D();
        rates[2] = getAPYRate90D();
        rates[3] = getAPYRate180D();
        emit StakingRatesUpdated(rates);
    }

    function _getTeamTiers()
        internal
        pure
        returns (IStaking.TeamTier[7] memory)
    {
        return [
            IStaking.TeamTier(getTeamThresholdTier7(), TEAM_REWARD_TIER_7),
            IStaking.TeamTier(getTeamThresholdTier6(), TEAM_REWARD_TIER_6),
            IStaking.TeamTier(getTeamThresholdTier5(), TEAM_REWARD_TIER_5),
            IStaking.TeamTier(getTeamThresholdTier4(), TEAM_REWARD_TIER_4),
            IStaking.TeamTier(getTeamThresholdTier3(), TEAM_REWARD_TIER_3),
            IStaking.TeamTier(getTeamThresholdTier2(), TEAM_REWARD_TIER_2),
            IStaking.TeamTier(getTeamThresholdTier1(), TEAM_REWARD_TIER_1)
        ];
    }

    // =========================================================================
    // PRIVATE FUNCTIONS
    // =========================================================================

    function _mintStakeRecord(
        address sender,
        uint160 _amount,
        uint8 _stakeIndex
    ) private {
        if (!isBindReferral(sender)) revert MustBindReferral();

        IStaking.RecordTT memory tsy;
        tsy.stakeTime = uint40(block.timestamp);
        tsy.tamount = uint160(totalSupply);
        t_supply.push(tsy);

        IStaking.Record memory order;
        order.stakeTime = uint40(block.timestamp);
        order.amount = _amount;
        order.status = false;
        order.stakeIndex = _stakeIndex;

        IStaking.Record[] storage cord = userStakeRecord[sender];
        uint256 stake_index = cord.length;
        cord.push(order);

        _updateTeamInvestmentValues(sender, _amount, true);
        _update(address(0), sender, _amount);

        emit Staked(
            sender,
            _amount,
            block.timestamp,
            stake_index,
            getStakePeriod(_stakeIndex)
        );
    }

    function _burn(
        uint256 index
    ) private returns (uint256 reward, uint256 amount) {
        address sender = msg.sender;
        IStaking.Record[] storage cord = userStakeRecord[sender];
        IStaking.Record storage user_record = cord[index];

        uint256 stakeTime = user_record.stakeTime;

        if (
            block.timestamp - stakeTime < getStakePeriod(user_record.stakeIndex)
        ) revert StakingPeriodNotMet();
        if (user_record.status) revert AlreadyWithdrawn();

        amount = user_record.amount;
        reward = _calculateStakeReward(user_record);
        user_record.status = true;

        _update(sender, address(0), amount);

        unchecked {
            userIndex[sender] = userIndex[sender] + 1;
        }
    }

    function _swapSYIForReward(
        uint256 calculatedReward
    ) private returns (uint256 usdtReceived, uint256 syiTokensUsed) {
        uint256 syiBalanceBefore = SYI.balanceOf(address(this));
        uint256 usdtBalanceBefore = IERC20(USDT).balanceOf(address(this));

        address[] memory swapPath = new address[](2);
        swapPath[0] = address(SYI);
        swapPath[1] = address(USDT);

        uint256 maxSYIInput = _calculateMaxSYIInput(
            calculatedReward,
            syiBalanceBefore
        );

        ROUTER.swapTokensForExactTokens(
            calculatedReward,
            maxSYIInput,
            swapPath,
            address(this),
            block.timestamp
        );

        uint256 syiBalanceAfter = SYI.balanceOf(address(this));
        usdtReceived =
            IERC20(USDT).balanceOf(address(this)) -
            usdtBalanceBefore;
        syiTokensUsed = syiBalanceBefore - syiBalanceAfter;
    }

    function _calculateMaxSYIInput(
        uint256 usdtNeeded,
        uint256 availableSYI
    ) private view returns (uint256 maxInput) {
        address pair = SYI.getUniswapV2Pair();
        try IUniswapV2Pair(pair).getReserves() returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32
        ) {
            (uint112 syiReserve, uint112 usdtReserve) = IUniswapV2Pair(pair)
                .token0() == address(SYI)
                ? (reserve0, reserve1)
                : (reserve1, reserve0);

            if (syiReserve > 0 && usdtReserve > 0) {
                uint256 maxSafeUsdtRequest = usdtReserve / 2;
                uint256 safeUsdtNeeded = usdtNeeded > maxSafeUsdtRequest
                    ? maxSafeUsdtRequest
                    : usdtNeeded;

                if (safeUsdtNeeded < usdtReserve) {
                    uint256 estimatedSYI = (safeUsdtNeeded * syiReserve) /
                        (usdtReserve - safeUsdtNeeded);
                    uint256 withSlippage = (estimatedSYI * 150) / 100;

                    maxInput = _min256(withSlippage, availableSYI);
                } else {
                    maxInput = availableSYI / 4;
                }
            } else {
                maxInput = availableSYI / 2;
            }
        } catch {
            maxInput = availableSYI / 2;
        }

        if (maxInput == 0 && availableSYI > 0) {
            maxInput = availableSYI / 10;
        }

        uint256 maxAllowedInput = availableSYI / 2;
        if (maxInput > maxAllowedInput) {
            maxInput = maxAllowedInput;
        }
    }

    function _distributeFriendReward(
        address _user,
        uint256 _interset
    ) private returns (uint256 fee) {
        unchecked {
            fee = (_interset * REFERRAL_REWARD_RATE) / PERCENTAGE_BASE;
        }
        address friend = getFriend(_user);
        if (friend != address(0)) {
            IERC20(USDT).transfer(friend, fee);
        } else {
            IERC20(USDT).transfer(rootAddress, fee);
        }
    }

    function _distributeTeamReward(
        address[] memory referralChain,
        uint256 _interset
    ) private returns (uint256 fee) {
        unchecked {
            fee = (_interset * MAX_TEAM_REWARD_RATE) / PERCENTAGE_BASE;
        }

        if (referralChain.length == 0) {
            IERC20(USDT).transfer(rootAddress, fee);

            address[7] memory emptyRecipients;
            uint256[7] memory emptyAmounts;
            for (uint8 j = 0; j < 7; j++) {
                emptyRecipients[j] = address(0);
                emptyAmounts[j] = 0;
            }

            emit TeamRewardDistributionCompleted(
                _interset,
                fee,
                0,
                fee,
                emptyRecipients,
                emptyAmounts,
                0
            );

            return fee;
        }

        uint8[] memory memberTiers = new uint8[](referralChain.length);

        for (uint256 i = 0; i < referralChain.length; ) {
            memberTiers[i] = _getUserTier(referralChain[i]);
            unchecked {
                ++i;
            }
        }

        (
            uint256 totalDistributed,
            address[7] memory tierRecipients,
            uint256[7] memory tierAmounts,
            uint8 activeTiers
        ) = _distributeHybridRewards(referralChain, memberTiers, _interset);

        uint256 marketingAmount = 0;
        if (totalDistributed < fee) {
            marketingAmount = fee - totalDistributed;
            IERC20(USDT).transfer(rootAddress, marketingAmount);
        }

        emit TeamRewardDistributionCompleted(
            _interset,
            fee,
            totalDistributed,
            marketingAmount,
            tierRecipients,
            tierAmounts,
            activeTiers
        );

        return fee;
    }

    function _distributeHybridRewards(
        address[] memory referralChain,
        uint8[] memory memberTiers,
        uint256 _interset
    )
        private
        returns (
            uint256 totalDistributed,
            address[7] memory tierRecipients,
            uint256[7] memory tierAmounts,
            uint8 activeTiers
        )
    {
        totalDistributed = 0;
        activeTiers = 0;

        for (uint8 j = 0; j < 7; j++) {
            tierRecipients[j] = address(0);
            tierAmounts[j] = 0;
        }

        bool[8] memory tierAllocated;
        uint256 cumulativeAllocatedRate = 0;

        for (uint256 i = 0; i < referralChain.length; ) {
            uint8 currentTier = memberTiers[i];

            if (
                currentTier > 0 &&
                !tierAllocated[currentTier] &&
                isPreacher(referralChain[i])
            ) {
                uint256 tierRewardRate = _getTierRewardRate(currentTier);
                uint256 actualRewardRate;

                if (tierRewardRate > cumulativeAllocatedRate) {
                    actualRewardRate = tierRewardRate - cumulativeAllocatedRate;
                } else {
                    actualRewardRate = 0;
                }

                if (actualRewardRate > 0) {
                    uint256 memberReward = (_interset * actualRewardRate) /
                        PERCENTAGE_BASE;

                    if (memberReward > 0) {
                        IERC20(USDT).transfer(referralChain[i], memberReward);
                        totalDistributed += memberReward;

                        tierRecipients[currentTier - 1] = referralChain[i];
                        tierAmounts[currentTier - 1] = memberReward;

                        activeTiers =
                            activeTiers |
                            uint8(1 << (currentTier - 1));

                        emit StrictDifferentialRewardPaid(
                            referralChain[i],
                            currentTier,
                            actualRewardRate,
                            memberReward,
                            cumulativeAllocatedRate,
                            tierRewardRate
                        );
                    }
                }

                tierAllocated[currentTier] = true;
                cumulativeAllocatedRate = tierRewardRate;
            } else if (
                currentTier > 0 &&
                !tierAllocated[currentTier] &&
                !isPreacher(referralChain[i])
            ) {
                emit PreacherCheckFailed(
                    referralChain[i],
                    currentTier,
                    "INSUFFICIENT_PREACHER_STATUS"
                );
            }

            unchecked {
                ++i;
            }
        }
    }

    function _calculateStakeReward(
        IStaking.Record storage stakeRecord
    ) private view returns (uint256 currentReward) {
        UD60x18 principalAmount = ud(stakeRecord.amount);
        uint40 stakeStartTime = stakeRecord.stakeTime;
        uint40 stakingDuration;

        unchecked {
            stakingDuration = uint40(block.timestamp) - stakeStartTime;
        }

        stakingDuration = _min40(
            stakingDuration,
            uint40(getStakePeriod(stakeRecord.stakeIndex))
        );

        if (stakingDuration == 0) {
            currentReward = UD60x18.unwrap(principalAmount);
        } else {
            UD60x18 baseInterestRate = ud(rates[stakeRecord.stakeIndex]);

            // Convert stakingDuration from seconds to the correct time unit (days for mainnet, minutes for testnet)
            uint256 compoundPeriods = stakingDuration / getCompoundTimeUnit();

            UD60x18 compoundedAmount = principalAmount.mul(
                baseInterestRate.powu(compoundPeriods)
            );
            currentReward = UD60x18.unwrap(compoundedAmount);
        }
    }

    function _validateStakeParameters(
        uint160 _amount,
        uint8 _stakeIndex
    ) private view {
        if (_amount > maxStakeAmount()) revert ExceedsMaxStakeAmount();
        if (_stakeIndex > MAX_STAKE_INDEX) revert InvalidStakeIndex();

        uint256 userCurrentTotal = principalBalance(msg.sender);
        if (userCurrentTotal + _amount > MAX_USER_TOTAL_STAKE) {
            revert ExceedsUserTotalStakeLimit();
        }
    }

    function _swapAndAddLiquidity(uint160 usdtAmount) private {
        IERC20(USDT).transferFrom(msg.sender, address(this), usdtAmount);

        address[] memory swapPath = new address[](2);
        swapPath[0] = address(USDT);
        swapPath[1] = address(SYI);

        uint256 syiBalanceBefore = SYI.balanceOf(address(this));
        uint256 usdtToSwap = usdtAmount / LIQUIDITY_SPLIT_DIVISOR;

        uint256 minOlaTokensOut = _calculateMinimumOutput(usdtToSwap);

        ROUTER.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            usdtToSwap,
            minOlaTokensOut,
            swapPath,
            address(this),
            block.timestamp
        );

        uint256 syiBalanceAfter = SYI.balanceOf(address(this));
        uint256 syiTokensReceived = syiBalanceAfter - syiBalanceBefore;

        uint256 remainingUsdt = usdtAmount - usdtToSwap;
        ROUTER.addLiquidity(
            address(USDT),
            address(SYI),
            remainingUsdt,
            syiTokensReceived,
            0,
            0,
            address(0),
            block.timestamp
        );
    }

    function _min40(uint40 a, uint40 b) private pure returns (uint40) {
        return a < b ? a : b;
    }

    function _min256(uint256 a, uint256 b) private pure returns (uint256) {
        return a < b ? a : b;
    }

    function _getUserTier(address user) private view returns (uint8 tier) {
        if (user == rootAddress || !isPreacher(user)) {
            return 0;
        }

        uint256 teamKPI = getTeamKpi(user);
        IStaking.TeamTier[7] memory tiers = _getTeamTiers();

        for (uint256 i = 0; i < tiers.length; ) {
            if (teamKPI >= tiers[i].threshold) {
                return uint8(7 - i);
            }
            unchecked {
                ++i;
            }
        }

        return 0;
    }

    function _getTierRewardRate(
        uint8 tier
    ) private pure returns (uint256 rate) {
        if (tier == 1) return 5;
        if (tier == 2) return 10;
        if (tier == 3) return 15;
        if (tier == 4) return 20;
        if (tier == 5) return 25;
        if (tier == 6) return 30;
        if (tier == 7) return 35;
        return 0;
    }

    function _calculateMinimumOutput(
        uint256 usdtAmountIn
    ) private view returns (uint256 minAmountOut) {
        address pair = SYI.getUniswapV2Pair();
        (uint112 reserve0, uint112 reserve1, ) = IUniswapV2Pair(pair)
            .getReserves();

        uint112 reserveUSDT;
        uint112 reserveSYI;

        address token0 = IUniswapV2Pair(pair).token0();
        if (token0 == address(USDT)) {
            reserveUSDT = reserve0;
            reserveSYI = reserve1;
        } else {
            reserveUSDT = reserve1;
            reserveSYI = reserve0;
        }

        uint256 expectedOutput = ROUTER.getAmountOut(
            usdtAmountIn,
            reserveUSDT,
            reserveSYI
        );

        uint256 priceImpact = (usdtAmountIn * BASIS_POINTS_DENOMINATOR) /
            reserveUSDT;

        uint256 slippageTolerance;
        if (priceImpact <= PRICE_IMPACT_THRESHOLD) {
            slippageTolerance = BASE_SLIPPAGE_TOLERANCE;
        } else {
            uint256 additionalSlippage = (priceImpact *
                BASE_SLIPPAGE_TOLERANCE) / PRICE_IMPACT_THRESHOLD;
            slippageTolerance = BASE_SLIPPAGE_TOLERANCE + additionalSlippage;

            if (slippageTolerance > MAX_SLIPPAGE_TOLERANCE) {
                slippageTolerance = MAX_SLIPPAGE_TOLERANCE;
            }
        }

        uint256 expectedOutputAfterFees = (expectedOutput *
            (BASIS_POINTS_DENOMINATOR - SYI_TOTAL_BUY_FEE_BPS)) /
            BASIS_POINTS_DENOMINATOR;

        minAmountOut =
            (expectedOutputAfterFees *
                (BASIS_POINTS_DENOMINATOR - slippageTolerance)) /
            BASIS_POINTS_DENOMINATOR;

        if (minAmountOut == 0) {
            minAmountOut = 1;
        }
    }

    function _recordWithdrawal(
        address user,
        uint256 stakeIndex,
        uint256 principalAmount,
        uint256 calculatedReward,
        uint256 usdtReceived,
        uint256 syiTokensUsed,
        uint256 referralFee,
        uint256 teamFee,
        uint256 userPayout,
        uint256 interestEarned
    ) private {
        uint40 withdrawalTime = uint40(block.timestamp);

        IStaking.WithdrawalRecord memory withdrawalRecord = IStaking
            .WithdrawalRecord({
                withdrawalTime: withdrawalTime,
                stakeIndex: stakeIndex,
                principalAmount: principalAmount,
                calculatedReward: calculatedReward,
                usdtReceived: usdtReceived,
                syiTokensUsed: syiTokensUsed,
                referralFee: referralFee,
                teamFee: teamFee,
                userPayout: userPayout,
                interestEarned: interestEarned
            });

        _userWithdrawalHistory[user].push(withdrawalRecord);

        emit RewardPaid(user, calculatedReward, withdrawalTime, stakeIndex);

        emit WithdrawalCompleted(
            user,
            stakeIndex,
            principalAmount,
            calculatedReward,
            usdtReceived,
            syiTokensUsed,
            referralFee,
            teamFee,
            userPayout,
            interestEarned,
            withdrawalTime
        );
    }

    // =========================================================================
    // ADMIN FUNCTIONS
    // =========================================================================

    function setSYI(address _syi) external onlyOwner {
        require(_syi != address(0), "SYI address cannot be zero");
        SYI = ISYI(_syi);
        SYI.approve(address(ROUTER), type(uint256).max);
        emit SYIContractSet(_syi);
    }

    function emergencyWithdrawSYI(
        address to,
        uint256 _amount
    ) external onlyOwner {
        SYI.transfer(to, _amount);
    }

    function emergencyWithdrawUSDT(
        address to,
        uint256 _amount
    ) external onlyOwner {
        IERC20(USDT).transfer(to, _amount);
    }

    function setFeeRecipient(address _feeRecipient) external onlyOwner {
        require(_feeRecipient != address(0), "Invalid fee recipient");
        address oldRecipient = feeRecipient;
        feeRecipient = _feeRecipient;
        emit FeeRecipientUpdated(oldRecipient, _feeRecipient);
    }
}

contracts/SYI-Staking/interfaces/IStakin.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

/**
 * @title IStaking
 * @notice Interface for SYI Protocol Staking Contract with advanced features
 * @dev Complete interface following Solidity Style Guide
 * @author SYI Protocol Team
 */
interface IStaking {
    // =========================================================================
    // ERRORS
    // =========================================================================

    /// @notice Thrown when caller is not an externally owned account
    error OnlyEOAAllowed();

    /// @notice Thrown when stake amount exceeds maximum allowed
    error ExceedsMaxStakeAmount();

    /// @notice Thrown when user's total stake would exceed limit
    error ExceedsUserTotalStakeLimit();

    /// @notice Thrown when stake index is invalid
    error InvalidStakeIndex();

    /// @notice Thrown when user must bind referral first
    error MustBindReferral();

    /// @notice Thrown when staking period requirements not met
    error StakingPeriodNotMet();

    /// @notice Thrown when stake has already been withdrawn
    error AlreadyWithdrawn();

    /// @notice Thrown when caller is not authorized
    error NotAuthorized();

    /// @notice Thrown when user tries to refer themselves
    error CannotReferSelf();

    /// @notice Thrown when referral is already bound
    error AlreadyBound();

    /// @notice Thrown when referral address is invalid
    error InvalidReferral();

    /// @notice Thrown when referral is already locked
    error AlreadyLocked();

    /// @notice Thrown when referrer is invalid
    error InvalidReferrer();

    // =========================================================================
    // STRUCTS
    // =========================================================================

    /**
     * @notice Individual stake record structure
     * @param stakeTime Timestamp when stake was created
     * @param amount Principal amount staked (in USDT)
     * @param status Whether stake has been withdrawn (true = withdrawn)
     * @param stakeIndex Staking tier (0=1day/0.3% daily, 1=7days/0.6% daily, 2=15days/1.0% daily, 3=30days/1.5% daily)
     */
    struct Record {
        uint40 stakeTime;
        uint160 amount;
        bool status;
        uint8 stakeIndex;
    }

    /**
     * @notice Supply tracking record for network activity
     * @param stakeTime Timestamp of the supply record
     * @param tamount Total supply at that time
     */
    struct RecordTT {
        uint40 stakeTime;
        uint160 tamount;
    }

    /**
     * @notice Team tier configuration for reward distribution
     * @param threshold Minimum team KPI required for this tier
     * @param rewardRate Reward rate percentage for this tier
     */
    struct TeamTier {
        uint256 threshold;
        uint256 rewardRate;
    }

    /**
     * @notice Complete withdrawal record for user history tracking
     * @param withdrawalTime Timestamp when withdrawal occurred
     * @param stakeIndex Index of the stake record that was withdrawn
     * @param principalAmount Original staked amount
     * @param calculatedReward Total calculated reward (principal + interest)
     * @param usdtReceived Actual USDT received from SYI swap
     * @param syiTokensUsed Amount of SYI tokens consumed in swap
     * @param referralFee Fee paid to referrer
     * @param teamFee Total team fees distributed
     * @param userPayout Net amount user actually received
     * @param interestEarned Interest earned (usdtReceived - principalAmount)
     */
    struct WithdrawalRecord {
        uint40 withdrawalTime;
        uint256 stakeIndex;
        uint256 principalAmount;
        uint256 calculatedReward;
        uint256 usdtReceived;
        uint256 syiTokensUsed;
        uint256 referralFee;
        uint256 teamFee;
        uint256 userPayout;
        uint256 interestEarned;
    }

    // =========================================================================
    // EVENTS
    // =========================================================================

    /**
     * @notice Emitted when a user stakes tokens
     * @param user Address of the staker
     * @param amount Amount staked
     * @param timestamp Block timestamp
     * @param index Stake record index
     * @param stakeTime Staking period duration
     */
    event Staked(
        address indexed user,
        uint256 amount,
        uint256 timestamp,
        uint256 index,
        uint256 stakeTime
    );

    /**
     * @notice Emitted when rewards are paid to a user (legacy event for compatibility)
     * @param user Address receiving rewards
     * @param reward Reward amount
     * @param timestamp Block timestamp
     * @param index Stake record index
     */
    event RewardPaid(
        address indexed user,
        uint256 reward,
        uint40 timestamp,
        uint256 index
    );

    /**
     * @notice Emitted when a user withdraws (unstakes) with complete details
     * @param user Address of the user
     * @param stakeIndex Index of the withdrawn stake record
     * @param principalAmount Original staked amount
     * @param calculatedReward Total calculated reward
     * @param usdtReceived Actual USDT received from swap
     * @param olaTokensUsed SYI tokens consumed in swap
     * @param referralFee Fee paid to referrer
     * @param teamFee Total team fees distributed
     * @param userPayout Net amount user received
     * @param interestEarned Interest earned
     * @param withdrawalTime Withdrawal timestamp
     */
    event WithdrawalCompleted(
        address indexed user,
        uint256 indexed stakeIndex,
        uint256 principalAmount,
        uint256 calculatedReward,
        uint256 usdtReceived,
        uint256 olaTokensUsed,
        uint256 referralFee,
        uint256 teamFee,
        uint256 userPayout,
        uint256 interestEarned,
        uint40 withdrawalTime
    );

    /**
     * @notice Emitted when referral relationship is bound
     * @param user User being referred
     * @param parent Referrer address
     */
    event BindReferral(address indexed user, address indexed parent);

    /**
     * @notice Emitted when a user binds a friend account for direct reward
     * @param user User who binds the friend
     * @param friend Friend address to receive direct rewards
     */
    event BindFriend(address indexed user, address indexed friend);

    /**
     * @notice Emitted for token transfers (minting/burning)
     * @param from Source address (address(0) for minting)
     * @param to Destination address (address(0) for burning)
     * @param amount Transfer amount
     */
    event Transfer(address indexed from, address indexed to, uint256 amount);

    /**
     * @notice Emitted when test mode is changed
     * @param enabled Whether test mode is enabled
     */
    event TestModeSet(bool enabled);

    /**
     * @notice Emitted when SYI contract address is set
     * @param syiAddress Address of the SYI contract
     */
    event SYIContractSet(address indexed syiAddress);

    /**
     * @notice Emitted when staking rates are updated
     * @param newRates Array of new per-second rates
     */
    event StakingRatesUpdated(uint256[4] newRates);

    event PresaleDurationUpdated(uint256 duration);

    /**
     * @notice Emitted when strict differential reward is paid
     * @param recipient Address receiving the reward
     * @param tier User's tier level (1-7)
     * @param actualRewardRate Actual reward rate applied (differential)
     * @param rewardAmount Actual reward amount paid
     * @param previousCumulativeRate Cumulative rate before this tier
     * @param currentTierRate Full rate for current tier
     */
    event StrictDifferentialRewardPaid(
        address indexed recipient,
        uint8 indexed tier,
        uint256 actualRewardRate,
        uint256 rewardAmount,
        uint256 previousCumulativeRate,
        uint256 currentTierRate
    );

    /**
     * @notice Comprehensive event for team reward distribution summary
     * @param interestAmount Total interest amount being distributed
     * @param totalTeamRewardPool Total team reward pool (35% of interest)
     * @param totalDistributed Total amount distributed to tier members
     * @param marketingAmount Amount sent to marketing address
     * @param tierRecipients Array of addresses receiving rewards [V1..V7] (address(0) if no recipient)
     * @param tierAmounts Array of reward amounts [V1..V7] (0 if no reward)
     * @param activeTiers Bitmap indicating which tiers received rewards (bit 0=V1, bit 1=V2, ... bit 6=V7)
     */
    event TeamRewardDistributionCompleted(
        uint256 interestAmount,
        uint256 totalTeamRewardPool,
        uint256 totalDistributed,
        uint256 marketingAmount,
        address[7] tierRecipients,
        uint256[7] tierAmounts,
        uint8 activeTiers
    );

    /**
     * @notice Event emitted when a user fails preacher check during team reward distribution
     * @param user User address that failed the check
     * @param tier Tier level the user would have qualified for
     * @param reason Reason for failure
     */
    event PreacherCheckFailed(
        address indexed user,
        uint8 indexed tier,
        string reason
    );

    // =========================================================================
    // CORE STAKING FUNCTIONS
    // =========================================================================

    /**
     * @notice Stakes USDT tokens and mints staking tokens
     * @param _amount Amount of USDT to stake
     * @param _stakeIndex Staking tier (0=1day/0.3% daily, 1=7days/0.6% daily, 2=15days/1.0% daily, 3=30days/1.5% daily)
     * @dev User must bind referral relationship via lockReferral() before staking
     * @dev Slippage protection is handled automatically within the contract
     */
    function stake(uint160 _amount, uint8 _stakeIndex) external;

    /**
     * @notice Unstakes tokens and distributes rewards
     * @param stakeIndex Index of the stake record to unstake
     * @return totalReward Total reward amount calculated
     */
    function unstake(uint256 stakeIndex) external returns (uint256 totalReward);

    // =========================================================================
    // WITHDRAWAL HISTORY FUNCTIONS
    // =========================================================================

    /**
     * @notice Gets withdrawal history for a user
     * @param user User address
     * @return Array of withdrawal records
     */
    function getWithdrawalHistory(
        address user
    ) external view returns (WithdrawalRecord[] memory);

    /**
     * @notice Gets the number of withdrawals for a user
     * @param user User address
     * @return count Number of withdrawals
     */
    function getWithdrawalCount(
        address user
    ) external view returns (uint256 count);

    /**
     * @notice Gets a specific withdrawal record
     * @param user User address
     * @param index Withdrawal record index
     * @return withdrawal Withdrawal record details
     */
    function getWithdrawalRecord(
        address user,
        uint256 index
    ) external view returns (WithdrawalRecord memory withdrawal);

    // =========================================================================
    // REFERRAL SYSTEM FUNCTIONS
    // =========================================================================

    /**
     * @notice Locks referral relationship for caller
     * @param _referrer The referrer to bind to (or address(0) for root)
     */
    function lockReferral(address _referrer) external;

    /**
     * @notice Locks a friend account to receive the 5% direct reward
     * @param _friend The friend address to bind
     */
    function lockFriend(address _friend) external;

    // =========================================================================
    // VIEW FUNCTIONS - USER INFORMATION
    // =========================================================================

    /// @notice Retrieves complete user information from staking contract
    /// @param user The address of the user to query
    /// @return totalStaked Total amount staked by the user
    /// @return teamKPI Team KPI value for the user
    /// @return referrer Address of the user's referrer
    /// @return hasLocked Whether the user has locked their referral relationship
    /// @return isPreacherStatus Whether the user has preacher (market maker) status
    function getUserInfo(
        address user
    )
        external
        view
        returns (
            uint128 totalStaked,
            uint128 teamKPI,
            address referrer,
            bool hasLocked,
            bool isPreacherStatus
        );

    /// @notice Checks if a user has preacher (market maker) status
    /// @param user Address to check
    /// @return True if user is a preacher, false otherwise
    /// @dev A preacher is a user who has staked at least 200 USDT
    function isPreacher(address user) external view returns (bool);

    /// @notice Checks if a user has bound their referral relationship
    /// @param user Address to check
    /// @return True if user has bound referral, false otherwise
    function isBindReferral(address user) external view returns (bool);

    /// @notice Gets the referrer of a user
    /// @param user Address to check
    /// @return The referrer address
    function getReferral(address user) external view returns (address);

    /**
     * @notice Gets the friend account bound by a user
     * @param user Address to check
     * @return The friend address
     */
    function getFriend(address user) external view returns (address);

    /// @notice Gets the team KPI value for a user (excluding self-investment)
    /// @param _user User address
    /// @return The team KPI value (total investment from team members only)
    function getTeamKpi(address _user) external view returns (uint256);

    /// @notice Gets the current network input value
    /// @return value The network input value
    function network1In() external view returns (uint256 value);

    /// @notice Gets the maximum stake amount allowed
    /// @return The maximum stake amount
    function maxStakeAmount() external view returns (uint256);

    /// @notice Gets the number of stake records for a user
    /// @param user User address
    /// @return count Number of stake records
    function stakeCount(address user) external view returns (uint256 count);

    /// @notice Gets the current balance (pending rewards) for a user
    /// @param account User address
    /// @return balance Total pending rewards
    function balanceOf(address account) external view returns (uint256 balance);

    /// @notice Gets the reward for a specific stake slot
    /// @param user User address
    /// @param index Stake record index
    /// @return reward Reward amount for the slot
    function rewardOfSlot(
        address user,
        uint8 index
    ) external view returns (uint256 reward);

    /// @notice Gets referral chain for a user
    /// @param user User address
    /// @param maxDepth Maximum depth to traverse
    /// @return Array of referrer addresses
    function getReferrals(
        address user,
        uint8 maxDepth
    ) external view returns (address[] memory);

    /// @notice Checks if a user can withdraw a specific stake
    /// @param user User address
    /// @param stakeIndex Index of the stake record to check
    /// @return canWithdraw True if the stake can be withdrawn, false otherwise
    function canWithdrawStake(
        address user,
        uint256 stakeIndex
    ) external view returns (bool canWithdraw);

    /// @notice Gets withdrawal status for all user stakes
    /// @param user User address
    /// @return stakeIndices Array of stake indices
    /// @return canWithdrawArray Array of withdrawal eligibility (true = can withdraw)
    /// @return timeRemaining Array of remaining time in seconds (0 if can withdraw)
    function getUserStakeWithdrawalStatus(
        address user
    )
        external
        view
        returns (
            uint256[] memory stakeIndices,
            bool[] memory canWithdrawArray,
            uint256[] memory timeRemaining
        );

    // =========================================================================
    // VIEW FUNCTIONS - NETWORK AND LIMITS
    // =========================================================================

    /**
     * @notice Gets remaining stake capacity for a user
     * @param user User address
     * @return remaining Remaining stake capacity in USDT
     */
    function getRemainingStakeCapacity(
        address user
    ) external view returns (uint256 remaining);

    /**
     * @notice Gets maximum user total stake limit
     * @return limit Maximum total stake limit per user
     */
    function getMaxUserTotalStake() external pure returns (uint256 limit);

    /**
     * @notice Gets the root address
     * @return rootAddress The root address
     */
    function getRootAddress() external view returns (address rootAddress);

    /**
     * @notice Gets the principal balance (original staked amount) for a user
     * @param account User address
     * @return balance Principal balance
     */
    function principalBalance(
        address account
    ) external view returns (uint256 balance);

    // =========================================================================
    // ADMINISTRATIVE FUNCTIONS
    // =========================================================================

    /**
     * @notice Sets the SYI token contract address (owner only)
     * @param _syi New SYI contract address
     */
    function setSYI(address _syi) external;

    // =========================================================================
    // UTILITY FUNCTIONS
    // =========================================================================

    /**
     * @notice Synchronizes USDT balance with the pool
     */
    function sync() external;

    /**
     * @notice Emergency withdrawal of SYI tokens (owner only)
     * @param to Recipient address
     * @param _amount Amount to withdraw
     */
    function emergencyWithdrawSYI(address to, uint256 _amount) external;

    /**
     * @notice Emergency withdrawal of USDT tokens (owner only)
     * @param to Recipient address
     * @param _amount Amount to withdraw
     */
    function emergencyWithdrawUSDT(address to, uint256 _amount) external;
}

