// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {IStaking} from "../interfaces/IStaking.sol";
import {ISYI} from "../interfaces/ISYI.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IUniswapV2Router02} from "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

/**
 * @title LiquidityStakingBase - Base implementation for LiquidityStaking contracts
 * @notice Base contract containing all shared logic for mainnet and testnet liquidity staking
 * @dev Environment-specific constants are defined as abstract functions
 */
abstract contract LiquidityStakingBase is ReentrancyGuard, Ownable {
    // =========================================================================
    // ABSTRACT FUNCTIONS - Environment specific constants
    // =========================================================================

    function getMinStakeDuration() internal pure virtual returns (uint256);

    // =========================================================================
    // STATE VARIABLES
    // =========================================================================

    address public immutable syiContract;
    address public immutable usdt;
    address public immutable lpToken;
    IStaking public immutable staking;
    IUniswapV2Router02 public immutable router;

    uint256 public constant MIN_REWARD_AMOUNT = 1000; // 0.001 USDT

    // SYI accumulation for delayed swapping
    uint256 public accumulatedSYI;

    // Staking Related Structs
    struct StakeInfo {
        uint256 amount;
        uint256 stakeTime;
        uint256 lastRewardTime;
        uint256 accumulatedReward;
    }

    struct RewardPool {
        uint256 totalRewards;
        uint256 rewardPerSecond;
        uint256 lastUpdateTime;
        uint256 rewardPerTokenStored;
        uint256 totalStaked;
        uint256 totalWeight;
        uint256 distributedRewards;
        uint256 pendingRewards;
    }

    // Mappings and Arrays
    mapping(address => StakeInfo) public stakes;
    address[] public stakers;
    mapping(address => bool) public isStaker;
    RewardPool public rewardPool;
    mapping(address => bool) public excludedFromStaking;

    uint256 private cachedTotalWeight;
    uint256 private cachedWeightTimestamp;

    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public userPendingRewards;

    // Events
    event Staked(address indexed user, uint256 amount, uint256 timestamp);
    event Unstaked(address indexed user, uint256 amount, uint256 reward);
    event RewardClaimed(address indexed user, uint256 reward);
    event RewardsDeposited(uint256 amount, uint256 newRewardRate);
    event SYIRewardsAccumulated(uint256 syiAmount, uint256 totalAccumulated);
    event SYISwappedToRewards(
        uint256 syiAmount,
        uint256 usdtAmount,
        uint256 newRewardRate
    );
    event AddressExcluded(address indexed account, bool excluded);

    // Errors
    error OnlyAdmin();
    error InvalidAddress();
    error InvalidAmount();
    error InsufficientStakeTime();
    error NoStakeFound();
    error ExcludedFromStaking();
    error TransferFailed();

    // Modifiers
    modifier onlyAdmin() {
        if (msg.sender != syiContract && msg.sender != owner())
            revert OnlyAdmin();
        _;
    }

    modifier updateReward(address account) {
        _updateRewardPool();
        if (account != address(0)) {
            _updateUserReward(account);
        }
        _;
    }

    // =========================================================================
    // CONSTRUCTOR
    // =========================================================================

    constructor(
        address _usdt,
        address _syiContract,
        address _lpToken,
        address _staking,
        address _marketingAddress,
        address _admin,
        address _router
    ) Ownable(_admin) {
        if (
            _usdt == address(0) ||
            _syiContract == address(0) ||
            _lpToken == address(0) ||
            _staking == address(0) ||
            _router == address(0)
        ) {
            revert InvalidAddress();
        }

        usdt = _usdt;
        syiContract = _syiContract;
        lpToken = _lpToken;
        staking = IStaking(_staking);
        router = IUniswapV2Router02(_router);

        rewardPool.lastUpdateTime = block.timestamp;

        if (_marketingAddress != address(0)) {
            excludedFromStaking[_marketingAddress] = true;
            emit AddressExcluded(_marketingAddress, true);
        }
        excludedFromStaking[address(this)] = true;
        excludedFromStaking[_syiContract] = true;
        emit AddressExcluded(address(this), true);
        emit AddressExcluded(_syiContract, true);
    }

    // =========================================================================
    // STAKING FUNCTIONS
    // =========================================================================

    function stake(
        uint256 amount
    ) external nonReentrant updateReward(msg.sender) {
        if (amount == 0) revert InvalidAmount();
        if (excludedFromStaking[msg.sender]) revert ExcludedFromStaking();

        if (!IERC20(lpToken).transferFrom(msg.sender, address(this), amount)) {
            revert TransferFailed();
        }

        StakeInfo storage userStake = stakes[msg.sender];

        if (!isStaker[msg.sender]) {
            stakers.push(msg.sender);
            isStaker[msg.sender] = true;
            userStake.stakeTime = block.timestamp;
            userStake.lastRewardTime = block.timestamp;
            userRewardPerTokenPaid[msg.sender] = _rewardPerToken();
            userPendingRewards[msg.sender] = 0;
        }

        userStake.amount += amount;
        rewardPool.totalStaked += amount;
        _updateCachedTotalWeight();

        emit Staked(msg.sender, amount, block.timestamp);

        _processAccumulatedSYI();
    }

    function unstake(
        uint256 amount
    ) external nonReentrant updateReward(msg.sender) {
        StakeInfo storage userStake = stakes[msg.sender];

        if (userStake.amount == 0) revert NoStakeFound();
        if (amount == 0 || amount > userStake.amount) revert InvalidAmount();
        if (block.timestamp < userStake.stakeTime + getMinStakeDuration()) {
            revert InsufficientStakeTime();
        }

        uint256 reward = _calculatePendingReward(msg.sender);
        if (reward > 0) {
            userPendingRewards[msg.sender] = 0;
            userRewardPerTokenPaid[msg.sender] = _rewardPerToken();

            userStake.accumulatedReward += reward;
            userStake.lastRewardTime = block.timestamp;
            rewardPool.distributedRewards += reward;
            rewardPool.pendingRewards = rewardPool.pendingRewards > reward
                ? rewardPool.pendingRewards - reward
                : 0;

            if (!IERC20(usdt).transfer(msg.sender, reward)) {
                revert TransferFailed();
            }
        }

        userStake.amount -= amount;
        rewardPool.totalStaked -= amount;

        if (userStake.amount == 0) {
            _removeStaker(msg.sender);
        }

        _updateCachedTotalWeight();

        if (!IERC20(lpToken).transfer(msg.sender, amount)) {
            revert TransferFailed();
        }

        emit Unstaked(msg.sender, amount, reward);

        _processAccumulatedSYI();
    }

    function claimReward() external nonReentrant updateReward(msg.sender) {
        uint256 reward = _calculatePendingReward(msg.sender);
        if (reward < MIN_REWARD_AMOUNT) return;

        StakeInfo storage userStake = stakes[msg.sender];

        userPendingRewards[msg.sender] = 0;
        userRewardPerTokenPaid[msg.sender] = _rewardPerToken();

        userStake.accumulatedReward += reward;
        userStake.lastRewardTime = block.timestamp;

        rewardPool.distributedRewards += reward;
        rewardPool.pendingRewards = rewardPool.pendingRewards > reward
            ? rewardPool.pendingRewards - reward
            : 0;

        if (!IERC20(usdt).transfer(msg.sender, reward)) {
            revert TransferFailed();
        }

        emit RewardClaimed(msg.sender, reward);

        _processAccumulatedSYI();
    }

    // =========================================================================
    // SYI CONTRACT FUNCTIONS
    // =========================================================================

    function depositRewards(
        uint256 amount
    ) external onlyAdmin updateReward(address(0)) {
        if (amount == 0) return;

        if (!IERC20(usdt).transferFrom(msg.sender, address(this), amount)) {
            revert TransferFailed();
        }

        rewardPool.totalRewards += amount;
        rewardPool.pendingRewards += amount;

        uint256 distributionPeriod = 7 days;

        rewardPool.rewardPerSecond =
            rewardPool.pendingRewards /
            distributionPeriod;
        emit RewardsDeposited(amount, rewardPool.rewardPerSecond);
    }

    function depositSYIRewards(uint256 syiAmount) external onlyAdmin {
        if (syiAmount == 0) return;

        if (
            !IERC20(syiContract).transferFrom(
                msg.sender,
                address(this),
                syiAmount
            )
        ) {
            revert TransferFailed();
        }

        // 只累积 SYI，不立即进行兑换
        accumulatedSYI += syiAmount;

        emit SYIRewardsAccumulated(syiAmount, accumulatedSYI);
    }

    // =========================================================================
    // INTERNAL FUNCTIONS
    // =========================================================================

    function _calculateWeight(
        uint256 amount,
        uint256 duration
    ) internal pure returns (uint256) {
        uint256 timeMultiplier = 1e18 + (duration * 1e18) / (365 days);
        return (amount * timeMultiplier) / 1e18;
    }

    function _updateRewardPool() internal {
        rewardPool.rewardPerTokenStored = _rewardPerToken();
        rewardPool.lastUpdateTime = block.timestamp;
    }

    function _rewardPerToken() internal view returns (uint256) {
        if (rewardPool.totalStaked == 0) {
            return rewardPool.rewardPerTokenStored;
        }

        uint256 timeDelta = block.timestamp - rewardPool.lastUpdateTime;
        uint256 additionalRewardPerToken = (timeDelta *
            rewardPool.rewardPerSecond *
            1e18) / rewardPool.totalStaked;

        return rewardPool.rewardPerTokenStored + additionalRewardPerToken;
    }

    function _updateUserReward(address account) internal {
        if (account == address(0)) return;

        StakeInfo storage userStake = stakes[account];
        if (userStake.amount == 0) return;

        uint256 currentRewardPerToken = _rewardPerToken();
        uint256 earnedRewards = _calculateEarnedRewards(
            account,
            currentRewardPerToken
        );

        userPendingRewards[account] += earnedRewards;
        userRewardPerTokenPaid[account] = currentRewardPerToken;
    }

    function _calculateEarnedRewards(
        address account,
        uint256 currentRewardPerToken
    ) internal view returns (uint256) {
        StakeInfo memory userStake = stakes[account];
        if (userStake.amount == 0) return 0;

        uint256 rewardPerTokenDelta = currentRewardPerToken -
            userRewardPerTokenPaid[account];

        uint256 stakeDuration = block.timestamp - userStake.stakeTime;
        uint256 timeMultiplier = 1e18 + (stakeDuration * 1e18) / (365 days);

        uint256 baseRewards = (userStake.amount * rewardPerTokenDelta) / 1e18;
        return (baseRewards * timeMultiplier) / 1e18;
    }

    function _calculatePendingReward(
        address account
    ) internal view returns (uint256) {
        StakeInfo memory userStake = stakes[account];
        if (userStake.amount == 0) return 0;

        uint256 currentRewardPerToken = _rewardPerToken();
        uint256 newlyEarned = _calculateEarnedRewards(
            account,
            currentRewardPerToken
        );

        return userPendingRewards[account] + newlyEarned;
    }

    function _calculateCurrentTotalWeight() internal view returns (uint256) {
        if (cachedWeightTimestamp == block.timestamp && cachedTotalWeight > 0) {
            return cachedTotalWeight;
        }

        uint256 currentTotalWeight = 0;

        for (uint256 i = 0; i < stakers.length; i++) {
            address staker = stakers[i];
            StakeInfo memory userStake = stakes[staker];

            if (userStake.amount > 0) {
                uint256 stakeDuration = block.timestamp - userStake.stakeTime;
                uint256 weight = _calculateWeight(
                    userStake.amount,
                    stakeDuration
                );
                currentTotalWeight += weight;
            }
        }

        return currentTotalWeight;
    }

    function _updateCachedTotalWeight() internal {
        cachedTotalWeight = _calculateCurrentTotalWeight();
        cachedWeightTimestamp = block.timestamp;
    }

    function _removeStaker(address account) internal {
        if (!isStaker[account]) return;

        for (uint256 i = 0; i < stakers.length; i++) {
            if (stakers[i] == account) {
                stakers[i] = stakers[stakers.length - 1];
                stakers.pop();
                isStaker[account] = false;
                break;
            }
        }
    }

    function _processAccumulatedSYI() internal {
        // 首先触发 FundRelay 分发
        try ISYI(syiContract).triggerFundRelayDistribution() {} catch {}

        if (accumulatedSYI == 0 || accumulatedSYI < 10 ether) return;

        uint256 syiToSwap = accumulatedSYI;
        accumulatedSYI = 0; // Reset accumulation

        uint256 usdtAmount = _swapSYIForUSDT(syiToSwap);

        if (usdtAmount > 0) {
            rewardPool.totalRewards += usdtAmount;
            rewardPool.pendingRewards += usdtAmount;

            uint256 distributionPeriod = 7 days;
            rewardPool.rewardPerSecond =
                rewardPool.pendingRewards /
                distributionPeriod;

            emit SYISwappedToRewards(
                syiToSwap,
                usdtAmount,
                rewardPool.rewardPerSecond
            );
        }
    }

    function _swapSYIForUSDT(
        uint256 syiAmount
    ) internal returns (uint256 usdtAmount) {
        if (syiAmount == 0) return 0;

        IERC20(syiContract).approve(address(router), syiAmount);

        address[] memory path = new address[](2);
        path[0] = syiContract;
        path[1] = usdt;

        uint256 initialBalance = IERC20(usdt).balanceOf(address(this));

        try
            router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                syiAmount,
                0, // accept any amount of USDT
                path,
                address(this),
                block.timestamp + 300
            )
        {
            uint256 finalBalance = IERC20(usdt).balanceOf(address(this));
            usdtAmount = finalBalance > initialBalance
                ? finalBalance - initialBalance
                : 0;
        } catch {
            // If swap fails, return 0
            usdtAmount = 0;
        }
    }

    // =========================================================================
    // VIEW FUNCTIONS
    // =========================================================================

    function getUserStakeInfo(
        address account
    )
        external
        view
        returns (
            uint256 stakedAmount,
            uint256 stakeTime,
            uint256 pendingReward,
            uint256 accumulatedReward,
            uint256 weight
        )
    {
        StakeInfo memory userStake = stakes[account];
        stakedAmount = userStake.amount;
        stakeTime = userStake.stakeTime;
        pendingReward = _calculatePendingReward(account);
        accumulatedReward = userStake.accumulatedReward;

        if (userStake.amount > 0) {
            uint256 stakeDuration = block.timestamp - userStake.stakeTime;
            weight = _calculateWeight(userStake.amount, stakeDuration);
        }
    }

    function getRewardPoolInfo()
        external
        view
        returns (
            uint256 totalRewards,
            uint256 rewardPerSecond,
            uint256 totalStaked,
            uint256 totalWeight,
            uint256 stakersCount,
            uint256 distributedRewards,
            uint256 pendingRewards
        )
    {
        totalRewards = rewardPool.totalRewards;
        rewardPerSecond = rewardPool.rewardPerSecond;
        totalStaked = rewardPool.totalStaked;
        totalWeight = _calculateCurrentTotalWeight();
        stakersCount = stakers.length;
        distributedRewards = rewardPool.distributedRewards;
        pendingRewards = rewardPool.pendingRewards;
    }

    function getStakersCount() external view returns (uint256) {
        return stakers.length;
    }

    function canUnstake(address account) external view returns (bool) {
        StakeInfo memory userStake = stakes[account];
        if (userStake.amount == 0) return false;
        return block.timestamp >= userStake.stakeTime + getMinStakeDuration();
    }

    function canWithdrawStake(
        address account
    )
        external
        view
        returns (bool canWithdraw, uint256 stakedAmount, uint256 timeRemaining)
    {
        StakeInfo memory userStake = stakes[account];
        stakedAmount = userStake.amount;

        if (stakedAmount == 0) {
            canWithdraw = false;
            timeRemaining = 0;
            return (canWithdraw, stakedAmount, timeRemaining);
        }

        uint256 minDuration = getMinStakeDuration();
        uint256 stakeEndTime = userStake.stakeTime + minDuration;

        if (block.timestamp >= stakeEndTime) {
            canWithdraw = true;
            timeRemaining = 0;
        } else {
            canWithdraw = false;
            timeRemaining = stakeEndTime - block.timestamp;
        }
    }

    function getMinStakeDurationExternal()
        external
        pure
        returns (uint256 duration)
    {
        return getMinStakeDuration();
    }

    // =========================================================================
    // ADMIN FUNCTIONS
    // =========================================================================

    function setExcluded(address account, bool excluded) external onlyAdmin {
        excludedFromStaking[account] = excluded;
        emit AddressExcluded(account, excluded);
    }

    function emergencyWithdraw(
        address token,
        uint256 amount
    ) external onlyAdmin {
        IERC20(token).transfer(syiContract, amount);
    }
}

contracts/LiquidityStaking/interfaces/IS.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

/**
 * @title IStaking
 * @notice Interface for XF Protocol Staking Contract with advanced features
 * @dev Complete interface following Solidity Style Guide
 * @author XF Protocol Team
 */
interface IStaking {
    // =========================================================================
    // ERRORS
    // =========================================================================

    /// @notice Thrown when caller is not an externally owned account
    error OnlyEOAAllowed();

    /// @notice Thrown when stake amount exceeds maximum allowed
    error ExceedsMaxStakeAmount();

    /// @notice Thrown when user's total stake would exceed limit
    error ExceedsUserTotalStakeLimit();

    /// @notice Thrown when stake index is invalid
    error InvalidStakeIndex();

    /// @notice Thrown when user must bind referral first
    error MustBindReferral();

    /// @notice Thrown when staking period requirements not met
    error StakingPeriodNotMet();

    /// @notice Thrown when stake has already been withdrawn
    error AlreadyWithdrawn();

    /// @notice Thrown when caller is not authorized
    error NotAuthorized();

    /// @notice Thrown when user tries to refer themselves
    error CannotReferSelf();

    /// @notice Thrown when referral is already bound
    error AlreadyBound();

    /// @notice Thrown when referral address is invalid
    error InvalidReferral();

    /// @notice Thrown when referral is already locked
    error AlreadyLocked();

    /// @notice Thrown when referrer is invalid
    error InvalidReferrer();

    // =========================================================================
    // STRUCTS
    // =========================================================================

    /**
     * @notice Individual stake record structure
     * @param stakeTime Timestamp when stake was created
     * @param amount Principal amount staked (in USDT)
     * @param status Whether stake has been withdrawn (true = withdrawn)
     * @param stakeIndex Staking tier (0=1day/0.3% daily, 1=7days/0.6% daily, 2=15days/1.0% daily, 3=30days/1.5% daily)
     */
    struct Record {
        uint40 stakeTime;
        uint160 amount;
        bool status;
        uint8 stakeIndex;
    }

    /**
     * @notice Supply tracking record for network activity
     * @param stakeTime Timestamp of the supply record
     * @param tamount Total supply at that time
     */
    struct RecordTT {
        uint40 stakeTime;
        uint160 tamount;
    }

    /**
     * @notice Team tier configuration for reward distribution
     * @param threshold Minimum team KPI required for this tier
     * @param rewardRate Reward rate percentage for this tier
     */
    struct TeamTier {
        uint256 threshold;
        uint256 rewardRate;
    }

    /**
     * @notice Complete withdrawal record for user history tracking
     * @param withdrawalTime Timestamp when withdrawal occurred
     * @param stakeIndex Index of the stake record that was withdrawn
     * @param principalAmount Original staked amount
     * @param calculatedReward Total calculated reward (principal + interest)
     * @param usdtReceived Actual USDT received from XF swap
     * @param olaTokensUsed Amount of XF tokens consumed in swap
     * @param referralFee Fee paid to referrer
     * @param teamFee Total team fees distributed
     * @param userPayout Net amount user actually received
     * @param interestEarned Interest earned (usdtReceived - principalAmount)
     */
    struct WithdrawalRecord {
        uint40 withdrawalTime;
        uint256 stakeIndex;
        uint256 principalAmount;
        uint256 calculatedReward;
        uint256 usdtReceived;
        uint256 olaTokensUsed;
        uint256 referralFee;
        uint256 teamFee;
        uint256 userPayout;
        uint256 interestEarned;
    }

    // =========================================================================
    // EVENTS
    // =========================================================================

    /**
     * @notice Emitted when a user stakes tokens
     * @param user Address of the staker
     * @param amount Amount staked
     * @param timestamp Block timestamp
     * @param index Stake record index
     * @param stakeTime Staking period duration
     */
    event Staked(
        address indexed user,
        uint256 amount,
        uint256 timestamp,
        uint256 index,
        uint256 stakeTime
    );

    /**
     * @notice Emitted when rewards are paid to a user (legacy event for compatibility)
     * @param user Address receiving rewards
     * @param reward Reward amount
     * @param timestamp Block timestamp
     * @param index Stake record index
     */
    event RewardPaid(
        address indexed user,
        uint256 reward,
        uint40 timestamp,
        uint256 index
    );

    /**
     * @notice Emitted when a user withdraws (unstakes) with complete details
     * @param user Address of the user
     * @param stakeIndex Index of the withdrawn stake record
     * @param principalAmount Original staked amount
     * @param calculatedReward Total calculated reward
     * @param usdtReceived Actual USDT received from swap
     * @param olaTokensUsed XF tokens consumed in swap
     * @param referralFee Fee paid to referrer
     * @param teamFee Total team fees distributed
     * @param userPayout Net amount user received
     * @param interestEarned Interest earned
     * @param withdrawalTime Withdrawal timestamp
     */
    event WithdrawalCompleted(
        address indexed user,
        uint256 indexed stakeIndex,
        uint256 principalAmount,
        uint256 calculatedReward,
        uint256 usdtReceived,
        uint256 olaTokensUsed,
        uint256 referralFee,
        uint256 teamFee,
        uint256 userPayout,
        uint256 interestEarned,
        uint40 withdrawalTime
    );

    /**
     * @notice Emitted when referral relationship is bound
     * @param user User being referred
     * @param parent Referrer address
     */
    event BindReferral(address indexed user, address indexed parent);

    /**
     * @notice Emitted when a user binds a friend account for direct reward
     * @param user User who binds the friend
     * @param friend Friend address to receive direct rewards
     */
    event BindFriend(address indexed user, address indexed friend);

    /**
     * @notice Emitted for token transfers (minting/burning)
     * @param from Source address (address(0) for minting)
     * @param to Destination address (address(0) for burning)
     * @param amount Transfer amount
     */
    event Transfer(address indexed from, address indexed to, uint256 amount);

    /**
     * @notice Emitted when test mode is changed
     * @param enabled Whether test mode is enabled
     */
    event TestModeSet(bool enabled);

    /**
     * @notice Emitted when XF contract address is set
     * @param xfAddress Address of the XF contract
     */
    event OLAContractSet(address indexed xfAddress);

    /**
     * @notice Emitted when staking rates are updated
     * @param newRates Array of new per-second rates
     */
    event StakingRatesUpdated(uint256[4] newRates);

    event PresaleDurationUpdated(uint256 duration);

    /**
     * @notice Emitted when strict differential reward is paid
     * @param recipient Address receiving the reward
     * @param tier User's tier level (1-7)
     * @param actualRewardRate Actual reward rate applied (differential)
     * @param rewardAmount Actual reward amount paid
     * @param previousCumulativeRate Cumulative rate before this tier
     * @param currentTierRate Full rate for current tier
     */
    event StrictDifferentialRewardPaid(
        address indexed recipient,
        uint8 indexed tier,
        uint256 actualRewardRate,
        uint256 rewardAmount,
        uint256 previousCumulativeRate,
        uint256 currentTierRate
    );

    /**
     * @notice Comprehensive event for team reward distribution summary
     * @param interestAmount Total interest amount being distributed
     * @param totalTeamRewardPool Total team reward pool (35% of interest)
     * @param totalDistributed Total amount distributed to tier members
     * @param marketingAmount Amount sent to marketing address
     * @param tierRecipients Array of addresses receiving rewards [V1..V7] (address(0) if no recipient)
     * @param tierAmounts Array of reward amounts [V1..V7] (0 if no reward)
     * @param activeTiers Bitmap indicating which tiers received rewards (bit 0=V1, bit 1=V2, ... bit 6=V7)
     */
    event TeamRewardDistributionCompleted(
        uint256 interestAmount,
        uint256 totalTeamRewardPool,
        uint256 totalDistributed,
        uint256 marketingAmount,
        address[7] tierRecipients,
        uint256[7] tierAmounts,
        uint8 activeTiers
    );

    /**
     * @notice Event emitted when a user fails preacher check during team reward distribution
     * @param user User address that failed the check
     * @param tier Tier level the user would have qualified for
     * @param reason Reason for failure
     */
    event PreacherCheckFailed(
        address indexed user,
        uint8 indexed tier,
        string reason
    );

    // =========================================================================
    // CORE STAKING FUNCTIONS
    // =========================================================================

    /**
     * @notice Stakes USDT tokens and mints staking tokens
     * @param _amount Amount of USDT to stake
     * @param _stakeIndex Staking tier (0=1day/0.3% daily, 1=7days/0.6% daily, 2=15days/1.0% daily, 3=30days/1.5% daily)
     * @dev User must bind referral relationship via lockReferral() before staking
     * @dev Slippage protection is handled automatically within the contract
     */
    function stake(uint160 _amount, uint8 _stakeIndex) external;

    /**
     * @notice Unstakes tokens and distributes rewards
     * @param stakeIndex Index of the stake record to unstake
     * @return totalReward Total reward amount calculated
     */
    function unstake(uint256 stakeIndex) external returns (uint256 totalReward);

    // =========================================================================
    // WITHDRAWAL HISTORY FUNCTIONS
    // =========================================================================

    /**
     * @notice Gets withdrawal history for a user
     * @param user User address
     * @return Array of withdrawal records
     */
    function getWithdrawalHistory(
        address user
    ) external view returns (WithdrawalRecord[] memory);

    /**
     * @notice Gets the number of withdrawals for a user
     * @param user User address
     * @return count Number of withdrawals
     */
    function getWithdrawalCount(
        address user
    ) external view returns (uint256 count);

    /**
     * @notice Gets a specific withdrawal record
     * @param user User address
     * @param index Withdrawal record index
     * @return withdrawal Withdrawal record details
     */
    function getWithdrawalRecord(
        address user,
        uint256 index
    ) external view returns (WithdrawalRecord memory withdrawal);

    // =========================================================================
    // REFERRAL SYSTEM FUNCTIONS
    // =========================================================================

    /**
     * @notice Locks referral relationship for caller
     * @param _referrer The referrer to bind to (or address(0) for root)
     */
    function lockReferral(address _referrer) external;

    /**
     * @notice Locks a friend account to receive the 5% direct reward
     * @param _friend The friend address to bind
     */
    function lockFriend(address _friend) external;

    // =========================================================================
    // VIEW FUNCTIONS - USER INFORMATION
    // =========================================================================

    /// @notice Retrieves complete user information from staking contract
    /// @param user The address of the user to query
    /// @return totalStaked Total amount staked by the user
    /// @return teamKPI Team KPI value for the user
    /// @return referrer Address of the user's referrer
    /// @return hasLocked Whether the user has locked their referral relationship
    /// @return isPreacherStatus Whether the user has preacher (market maker) status
    function getUserInfo(
        address user
    )
        external
        view
        returns (
            uint128 totalStaked,
            uint128 teamKPI,
            address referrer,
            bool hasLocked,
            bool isPreacherStatus
        );

    /// @notice Checks if a user has preacher (market maker) status
    /// @param user Address to check
    /// @return True if user is a preacher, false otherwise
    /// @dev A preacher is a user who has staked at least 200 USDT
    function isPreacher(address user) external view returns (bool);

    /// @notice Checks if a user has bound their referral relationship
    /// @param user Address to check
    /// @return True if user has bound referral, false otherwise
    function isBindReferral(address user) external view returns (bool);

    /// @notice Gets the referrer of a user
    /// @param user Address to check
    /// @return The referrer address
    function getReferral(address user) external view returns (address);

    /**
     * @notice Gets the friend account bound by a user
     * @param user Address to check
     * @return The friend address
     */
    function getFriend(address user) external view returns (address);

    /// @notice Gets the team KPI value for a user (excluding self-investment)
    /// @param _user User address
    /// @return The team KPI value (total investment from team members only)
    function getTeamKpi(address _user) external view returns (uint256);

    /// @notice Gets the current network input value
    /// @return value The network input value
    function network1In() external view returns (uint256 value);

    /// @notice Gets the maximum stake amount allowed
    /// @return The maximum stake amount
    function maxStakeAmount() external view returns (uint256);

    /// @notice Gets the number of stake records for a user
    /// @param user User address
    /// @return count Number of stake records
    function stakeCount(address user) external view returns (uint256 count);

    /// @notice Gets the current balance (pending rewards) for a user
    /// @param account User address
    /// @return balance Total pending rewards
    function balanceOf(address account) external view returns (uint256 balance);

    /// @notice Gets the reward for a specific stake slot
    /// @param user User address
    /// @param index Stake record index
    /// @return reward Reward amount for the slot
    function rewardOfSlot(
        address user,
        uint8 index
    ) external view returns (uint256 reward);

    /// @notice Gets referral chain for a user
    /// @param user User address
    /// @param maxDepth Maximum depth to traverse
    /// @return Array of referrer addresses
    function getReferrals(
        address user,
        uint8 maxDepth
    ) external view returns (address[] memory);

    /// @notice Checks if a user can withdraw a specific stake
    /// @param user User address
    /// @param stakeIndex Index of the stake record to check
    /// @return canWithdraw True if the stake can be withdrawn, false otherwise
    function canWithdrawStake(
        address user,
        uint256 stakeIndex
    ) external view returns (bool canWithdraw);

    /// @notice Gets withdrawal status for all user stakes
    /// @param user User address
    /// @return stakeIndices Array of stake indices
    /// @return canWithdrawArray Array of withdrawal eligibility (true = can withdraw)
    /// @return timeRemaining Array of remaining time in seconds (0 if can withdraw)
    function getUserStakeWithdrawalStatus(
        address user
    )
        external
        view
        returns (
            uint256[] memory stakeIndices,
            bool[] memory canWithdrawArray,
            uint256[] memory timeRemaining
        );

    // =========================================================================
    // VIEW FUNCTIONS - NETWORK AND LIMITS
    // =========================================================================

    /**
     * @notice Gets remaining stake capacity for a user
     * @param user User address
     * @return remaining Remaining stake capacity in USDT
     */
    function getRemainingStakeCapacity(
        address user
    ) external view returns (uint256 remaining);

    /**
     * @notice Gets maximum user total stake limit
     * @return limit Maximum total stake limit per user
     */
    function getMaxUserTotalStake() external pure returns (uint256 limit);

    /**
     * @notice Gets the root address
     * @return rootAddress The root address
     */
    function getRootAddress() external view returns (address rootAddress);

    /**
     * @notice Gets the principal balance (original staked amount) for a user
     * @param account User address
     * @return balance Principal balance
     */
    function principalBalance(
        address account
    ) external view returns (uint256 balance);

    // =========================================================================
    // ADMINISTRATIVE FUNCTIONS
    // =========================================================================

    /**
     * @notice Sets the XF token contract address (owner only)
     * @param _xf New XF contract address
     */
    function setOLA(address _xf) external;

    // =========================================================================
    // UTILITY FUNCTIONS
    // =========================================================================

    /**
     * @notice Synchronizes USDT balance with the pool
     */
    function sync() external;

    /**
     * @notice Emergency withdrawal of XF tokens (owner only)
     * @param to Recipient address
     * @param _amount Amount to withdraw
     */
    function emergencyWithdrawOLA(address to, uint256 _amount) external;

    /**
     * @notice Emergency withdrawal of USDT tokens (owner only)
     * @param to Recipient address
     * @param _amount Amount to withdraw
     */
    function emergencyWithdrawUSDT(address to, uint256 _amount) external;
}

// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title ISYI
 * @notice Interface for SYI Token - DeFi token with dynamic fees and LP rewards
 * @dev Complete interface following Solidity Style Guide
 * @author SYI Protocol Team
 */
interface ISYI is IERC20 {
    // =========================================================================
    // ERRORS
    // =========================================================================

    /// @notice Thrown when user tries to sell within cold period
    error StillInColdPeriod();

    /// @notice Thrown when transaction exceeds maximum allowed amount
    error ExceedsMaxTransaction();

    /// @notice Thrown when address is blacklisted
    error AddressBlacklisted();

    /// @notice Thrown when caller is not the staking contract
    error OnlyStakingContract();

    /// @notice Thrown when pool update is called too frequently
    error UpdateTooFrequent();

    /// @notice Thrown when input amount is insufficient
    error InsufficientInputAmount();

    /// @notice Thrown when output amount is insufficient
    error InsufficientOutputAmount();

    /// @notice Thrown when liquidity is insufficient
    error InsufficientLiquidity();

    /// @notice Thrown when contract calls are not allowed
    error ContractCallNotAllowed();

    /// @notice Thrown when array length exceeds maximum
    error ArrayLengthExceeded();

    /// @notice Thrown when zero address is provided
    error ZeroAddress();

    /// @notice Thrown when token is already launched
    error AlreadyLaunched();

    /// @notice Thrown when maximum burn amount is reached
    error MaxBurnAmountReached();

    /// @notice Thrown when fee rate is invalid
    error InvalidFeeRate();

    /// @notice Thrown when balance is insufficient
    error InsufficientBalance();
    // =========================================================================
    // STRUCTS
    // =========================================================================

    /**
     * @notice Pool status information for liquidity tracking
     * @param balance USDT reserve balance in the pool
     * @param timestamp Last update timestamp
     */
    struct PoolStatus {
        uint112 balance;
        uint40 timestamp;
    }

    /**
     * @notice Batch distribution record for LP rewards
     * @param batchId Unique identifier for this distribution batch
     * @param timestamp When this batch was distributed
     * @param totalRewards Total USDT rewards distributed in this batch
     * @param participantCount Number of LP holders who received rewards
     * @param totalLPSupply Total LP supply at time of distribution
     * @param distributedPerLP USDT rewards per LP token (scaled by 1e18)
     * @param isComplete Whether the batch distribution was completed
     * @param accumulatedRewards Total accumulated rewards carried from previous batches
     */
    struct BatchDistribution {
        uint256 batchId;
        uint40 timestamp;
        uint256 totalRewards;
        uint256 participantCount;
        uint256 totalLPSupply;
        uint256 distributedPerLP;
        bool isComplete;
        uint256 accumulatedRewards;
    }

    // =========================================================================
    // EVENTS
    // =========================================================================

    // === CORE PROTOCOL EVENTS ===
    event TradingEnabled(uint40 indexed timestamp);
    event TokensRecycled(uint256 indexed amount);
    event PoolReserveUpdated(uint112 indexed newReserve, uint40 timestamp);

    // === FEE AND CONFIGURATION EVENTS ===
    event ColdPeriodUpdated(uint40 indexed newPeriod);
    event FeeSwapThresholdUpdated(uint256 indexed newThreshold);
    event MarketingAddressUpdated(address indexed newAddress);

    // === PROFIT DISTRIBUTION EVENTS ===
    event ProfitDistributed(
        address indexed user,
        address indexed referrer,
        uint256 amount
    );
    event LiquidityAdded(uint256 tokenAmount, uint256 baseTokenAmount);

    // === ACCESS CONTROL EVENTS ===
    event BlacklistUpdated(address indexed account, bool indexed isBlacklisted);
    event AddedToWhitelist(address indexed account);
    event RemovedFromWhitelist(address indexed account);
    event TrustedRoutersAdded(address[] routers);

    // === LP DISTRIBUTION EVENTS ===
    event LPRewardDeposited(uint256 indexed amount, uint256 timestamp);
    event LpRewardsCombined(uint256 indexed combinedAmount, uint256 timestamp);
    event LPDistributionTriggered(
        uint256 indexed distributedCount,
        uint256 gasUsed
    );
    event LPRewardDistributed(
        address indexed holder,
        uint256 indexed reward,
        uint256 lpBalance,
        uint256 totalEligibleLP
    );

    event AutoDistributionStatusChanged(bool indexed enabled);

    // === BATCH DISTRIBUTION EVENTS ===
    event BatchDistributionStarted(
        uint256 indexed batchId,
        uint256 totalRewards,
        uint256 accumulatedRewards,
        uint256 participantCount
    );
    event BatchDistributionCompleted(
        uint256 indexed batchId,
        uint256 totalDistributed,
        uint256 participantCount,
        uint256 remainingRewards
    );
    event RewardsAccumulated(
        uint256 indexed amount,
        uint256 newAccumulatedTotal,
        string source
    );
    event AutoLPRewardsStatusChanged(bool indexed enabled);
    event TokenDistributorUpdated(address indexed distributor);
    event InitialWhitelistSet();
    event UniswapV2PairCreated(address indexed pair);

    /// @notice Emitted when tokens are burned during buy transactions
    /// @param amount Amount of tokens burned
    event TokensBurned(uint256 amount);

    /// @notice Emitted when liquidity is permanently locked
    /// @param tokenAmount SYI tokens used for liquidity
    /// @param usdtAmount USDT tokens used for liquidity
    /// @param lpTokensLocked LP tokens sent to dead address
    event LiquidityLocked(
        uint256 tokenAmount,
        uint256 usdtAmount,
        uint256 lpTokensLocked
    );

    /// @notice Emitted when buy transaction is processed
    /// @param buyer Address of token buyer
    /// @param amount Total token amount bought
    /// @param burnFee Amount burned
    /// @param liquidityFee Amount used for liquidity
    event BuyProcessed(
        address indexed buyer,
        uint256 amount,
        uint256 burnFee,
        uint256 liquidityFee
    );

    /// @notice Emitted when sell transaction is processed
    /// @param seller Address of token seller
    /// @param amount Total token amount sold
    /// @param marketingFee Marketing fee charged
    /// @param lpFee LP fee charged
    /// @param profitTax Profit tax in USDT
    event SellProcessed(
        address indexed seller,
        uint256 amount,
        uint256 marketingFee,
        uint256 lpFee,
        uint256 profitTax
    );

    /// @notice Emitted when LP fees are processed into USDT rewards
    /// @param tokenAmount LP fee tokens processed
    /// @param usdtReceived USDT added to rewards pool
    event LpFeeProcessed(uint256 tokenAmount, uint256 usdtReceived);

    /// @notice Emitted when batch LP distribution completes
    /// @param holdersProcessed Number of holders processed
    /// @param totalDistributed Total USDT distributed
    /// @param completed Whether distribution completed successfully
    event LpBatchDistribution(
        uint256 holdersProcessed,
        uint256 totalDistributed,
        bool completed
    );

    /// @notice Emitted when profit tax is distributed
    /// @param user User who triggered the tax
    /// @param referrer Referrer address (zero if none)
    /// @param referrerAmount Amount sent to referrer
    /// @param marketingAmount Amount sent to marketing
    event ProfitTaxDistributed(
        address indexed user,
        address indexed referrer,
        uint256 referrerAmount,
        uint256 marketingAmount
    );

    /// @notice Emitted when LP holder is added to tracking
    /// @param holder New LP holder address
    /// @param lpBalance Initial LP balance
    event LpHolderAdded(address indexed holder, uint256 lpBalance);

    /// @notice Emitted when LP holder is removed from tracking
    /// @param holder Removed LP holder address
    event LpHolderRemoved(address indexed holder);

    /// @notice Emitted when marketing fees are processed
    /// @param amount Token amount processed to USDT
    event MarketingFeesProcessed(uint256 amount);

    /// @notice Emitted when LP rewards are accumulated for future distribution
    /// @param amount USDT amount accumulated
    /// @param accumulatedPerLP New accumulated rewards per LP token
    event LpRewardsAccumulated(uint256 amount, uint256 accumulatedPerLP);

    /// @notice Emitted when user's accumulated rewards are updated
    /// @param user User address
    /// @param newRewards New rewards added
    /// @param totalAccumulated Total accumulated rewards for user
    event UserRewardsUpdated(
        address indexed user,
        uint256 newRewards,
        uint256 totalAccumulated
    );

    // =========================================================================
    // CORE FUNCTIONS
    // =========================================================================

    /**
     * @notice Enables trading and launches the token
     */
    function setPresale() external;

    /**
     * @notice Sets the presale duration
     * @param _duration New presale duration in seconds
     */
    function setPresaleDuration(uint256 _duration) external;

    /**
     * @notice Sets the presale active status
     * @param _active Whether presale is active
     */
    function setPresaleActive(bool _active) external;

    /**
     * @notice Recycles tokens from liquidity pool to staking contract
     * @param amount Amount of tokens to recycle
     */
    function recycle(uint256 amount) external;

    /**
     * @notice Trigger FundRelay fee distribution
     */
    function triggerFundRelayDistribution() external;

    /**
     * @notice Trigger processing of accumulated fees
     * @dev Can be called by staking contracts or owner for manual maintenance
     */
    function triggerFeeProcessing() external;

    /**
     * @notice Updates pool reserve information
     */
    function updatePoolReserve() external;

    // =========================================================================
    // CONFIGURATION FUNCTIONS
    // =========================================================================

    /**
     * @notice Sets the cold period between buy and sell transactions
     * @param newColdTime New cold period in seconds
     */
    function updateTradingParams(
        uint40 newColdTime,
        uint256 newSwapThreshold
    ) external;

    /**
     * @notice Updates marketing address
     * @param newAddress New marketing address
     */
    function setMarketingAddress(address newAddress) external;

    /**
     * @notice Updates staking contract address
     * @param newAddress New staking contract address
     */
    function setStaking(address newAddress) external;

    /**
     * @notice Sets auto LP rewards distribution status
     * @param enabled Whether to enable automatic LP rewards distribution
     */
    function setAutoLPRewardsEnabled(bool enabled) external;

    /**
     * @notice Sets auto distribution status
     * @param enabled Whether to enable auto distribution
     */
    function setAutoDistributeEnabled(bool enabled) external;

    /**
     * @notice Creates the Uniswap V2 pair for this token
     */
    function createUniswapV2Pair() external;

    /**
     * @notice Sets initial whitelist for key addresses
     */
    function setInitialWhitelist() external;

    // =========================================================================
    // ACCESS CONTROL FUNCTIONS
    // =========================================================================

    /**
     * @notice Updates blacklist status for multiple addresses
     * @param addresses Array of addresses to update
     * @param shouldBlacklist Whether to blacklist the addresses
     */
    function updateBlacklist(
        address[] calldata addresses,
        bool shouldBlacklist
    ) external;

    /**
     * @notice Adds a single address to the whitelist
     * @param account Address to add to whitelist
     */
    function addToWhitelist(address account) external;

    /**
     * @notice Removes a single address from the whitelist
     * @param account Address to remove from whitelist
     */
    function removeFromWhitelist(address account) external;

    /**
     * @notice Adds multiple addresses to the whitelist
     * @param accounts Array of addresses to add to whitelist
     */
    function batchAddToWhitelist(address[] calldata accounts) external;

    // =========================================================================
    // LP DISTRIBUTION FUNCTIONS
    // =========================================================================

    /**
     * @notice Gets LP holders information
     * @return holders Array of LP holder addresses
     * @return count Total number of LP holders
     */
    function getLPHoldersInfo()
        external
        view
        returns (address[] memory holders, uint256 count);

    /**
     * @notice Unified LP rewards distribution function
     * @dev Handles fee processing, external USDT deposits, and distribution in one call
     * @param depositAmount Optional USDT amount to deposit from caller (0 to skip deposit)
     * @param forceDistribution Whether to bypass cooldown and threshold checks
     * @return success Whether the operation completed without errors
     * @return processedHolders Number of LP holders that received rewards
     * @return distributedAmount Total USDT amount distributed to holders
     * @return completed Whether the distribution batch completed fully
     */
    function distributeRewards(
        uint256 depositAmount,
        bool forceDistribution
    )
        external
        returns (
            bool success,
            uint256 processedHolders,
            uint256 distributedAmount,
            bool completed
        );

    /**
     * @notice Manually remove invalid LP holders (owner only)
     * @param invalidHolders Array of addresses to remove from LP holder list
     */
    function cleanupLPHolders(address[] calldata invalidHolders) external;

    /**
     * @notice Manually distributes profit tax from contract's USDT balance (Owner only)
     * @param usdtAmount Amount of USDT to distribute as profit tax
     * @param referrer Address of referrer (zero address if none)
     * @param user Address of user for event emission
     */
    function distributeProfitTax(
        uint256 usdtAmount,
        address referrer,
        address user
    ) external;

    // =========================================================================
    // VIEW FUNCTIONS
    // =========================================================================

    function getMarketingFeeRate() external view returns (uint256);
    function getMarketingAddress() external view returns (address);
    function getStakingContract() external view returns (address);
    function getUSDTAddress() external view returns (address);
    function getUniswapV2Pair() external view returns (address);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountOut);

    function getUSDTReserve() external view returns (uint112);

    function isPresaleEnabled() external view returns (bool);

    /**
     * @notice Gets comprehensive presale status information
     * @return active Whether presale is active
     * @return startTime When presale started
     * @return duration How long presale lasts
     * @return remainingTime Time remaining in presale
     * @return isInPresale Whether current time is within presale period
     */
    function getPresaleStatus()
        external
        view
        returns (
            bool active,
            uint256 startTime,
            uint256 duration,
            uint256 remainingTime,
            bool isInPresale
        );

    function getColdTime() external view returns (uint40);
    function isWhitelisted(address account) external view returns (bool);
    function isBlacklisted(address account) external view returns (bool);

    function shouldSwapTokensForFund(
        uint256 amount
    ) external view returns (bool);

    /**
     * @notice Gets token balance for an account (standard naming)
     * @param account Address to check balance for
     * @return balance Token balance of the account
     */
    function getTokenBalance(
        address account
    ) external view returns (uint256 balance);

    /**
     * @notice Gets total LP supply held by valid LP holders only
     * @return totalValidSupply Total LP tokens held by valid holders
     */
    function getTotalValidLpSupply() external view returns (uint256);
}

contracts/LiquidityStaking/mainnet/Liqui.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import {LiquidityStakingBase} from "../abstract/LiquidityStakingBase.sol";

/**
 * @title LiquidityStaking - Mainnet implementation of LiquidityStaking
 * @notice Production environment liquidity staking with mainnet-specific constants
 */
contract LiquidityStaking is LiquidityStakingBase {
    // =========================================================================
    // CONSTRUCTOR
    // =========================================================================

    constructor(
        address _usdt,
        address _syiContract,
        address _lpToken,
        address _staking,
        address _marketingAddress,
        address _admin,
        address _router
    ) LiquidityStakingBase(_usdt, _syiContract, _lpToken, _staking, _marketingAddress, _admin, _router) {}

    // =========================================================================
    // ENVIRONMENT SPECIFIC CONSTANTS - MAINNET VALUES
    // =========================================================================

    // Minimum stake duration - 24 hours for mainnet
    function getMinStakeDuration() internal pure override returns (uint256) {
        return 24 hours;
    }
}

